package com.meihaifeng.test;import static java.lang.Thread.sleep;/** * 浙江卓锐科技股份有限公司 * * @author meihf * @date 2016/11/4 * @description */public class RunnableTest implements Runnable{    private  String name;    public RunnableTest(String name){        this.name=name;    }    @Override    public void run(){        for (int i=0;i<5;i++){            System.out.println(name+"运行"+i);            try {                sleep((int)Math.random()*10);            } catch (InterruptedException e) {                e.printStackTrace();            }        }    }    public static void main(String args[]) {            RunnableTest test1 = new RunnableTest("A");            Thread thread1 = new Thread(test1);            thread1.start();            RunnableTest test2 = new RunnableTest("B");            Thread thread2 = new Thread(test2);            thread2.start();        }}