package com.meihaifeng.web;import com.meihaifeng.entity.RestMessage;import com.meihaifeng.entity.User;import com.meihaifeng.service.UserServiceImpl;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.Map;/** * 浙江卓锐科技股份有限公司 * * @author meihf * @date 2016/11/10 * @description */@RestController@RequestMapping("aop")public class AopController {    private static final Log LOG = LogFactory.getLog("AopController.class");    @Autowired    UserServiceImpl userService;    @RequestMapping("/getUserLog")    public Map<String,Object> getUserLog(){        Map<String,Object> map = userService.getUserLog(1);        return map;    }    @RequestMapping("/getUserLog2")    public RestMessage getUserLog2() {        RestMessage restMessage = userService.getUserLog2();        return restMessage;    }    @RequestMapping("/getUserLog3")    public User getUserLog3(){        User user = userService.getUserLog3();        return user;    }    @RequestMapping("getUserLog4")    public User getUserLog4(){        User info = userService.getUserLog4();        return info;    }    @RequestMapping("getUserLog5")    public User getUserLog5(){        User user = userService.getUserLog5();        return user;    }    /**     * 这个事在启动spring的时候就加载的缓存     */    @RequestMapping("getRedisCache")    public Map<Integer,User> getRedisCache(){        Map<Integer,User> userMap = userService.getRedisCache();        return userMap;    }}